vec2 pow2(vec2 n){
	float x = n.x*n.x-n.y*n.y;
    float y = 2.0*n.x*n.y;
	return vec2(x,y);
}

vec2 plus(vec2 n, vec2 m){
	float x = n.x+m.x;
    float y = n.y+m.y;
	return vec2(x,y);
}

vec2 pow2plus(vec2 n, vec2 m){
	vec2 p2 = pow2(n);
    vec2 result = plus(p2,m);
	return result;
}


float isInMandelbrot(float x, float y){
    vec2 z0 = vec2(0.0,0.0);
    vec2 m = vec2(x,y);
    vec2 zn = z0;
    for(int i=0; i<60; i++){
        zn = pow2plus(zn,m);
    }

	return length(zn);
}

void mainImage( out vec4 O, vec2 u )
{

	float t = mod(iTime,47.), v=0.;
    t = min(t, 47.-t);
    float  scale = 1./(pow(t,t/6.));

    vec2  R = iResolution.xy, U;
    O -= O;

    for (int k=0; k<9;k++) { 
        U  = ( u+.33*vec2(k%3-1,k/3-1) - .5* R ) / R.y*scale;
        v  = isInMandelbrot(U.x-0.7652, U.y+0.1);
        O +=  sin(3.14*v);
    }
    O = .5 + .5* O/9.;
}
