// Some doc there: http://www.shaderific.com/features
// Ggl iq distance function

mat2 rotation (float a) {
	float c = cos(a);
    float s = sin(a);
   	return mat2(c, s, -s, c);
}

float sphere (vec3 pos, float radius) {
	return length(pos) - radius;
}

float cylinder (vec3 pos, float radius) {
	return length(pos.xy) - radius;
}

float box (vec3 pos, float radius) {
    pos = abs(pos);
    return max(pos.x, max(pos.y, pos.z)) - radius;
}

float map (vec3 pos) {
    pos.xz *= rotation(0.5*iTime);
    pos.yz *= rotation(0.7*iTime);
    pos.xy *= rotation(0.3*sin(iTime)*pos.z);

    float size = 3.;
    pos = mod(pos, size) - size/2.;


    float cyl_radius  = .5*(sin(0.7*iTime)*0.5+0.5);
    float cyl2_radius = .5*(cos(0.6*iTime)*0.5+0.5);
    float cyl3_radius = .5*(sin(0.5*iTime)*0.5+0.5);
    float box_radius  = .6*(cos(iTime)*0.5+0.5);

    float geometry = box(pos, box_radius);
    geometry = min(geometry, cylinder(pos.xyz, cyl_radius));
    geometry = min(geometry, cylinder(pos.xzy, cyl2_radius));
    geometry = min(geometry, cylinder(pos.yzx, cyl3_radius));
    return geometry;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 position = fragCoord/iResolution.xy;
    position = position*2. - 1.;
    position.x *= iResolution.x/iResolution.y;

    vec3 eye = vec3(0., 0., -2);
    vec3 ray = normalize(vec3(position, 1.));
    float shade = 0.;
    for (int index=0; index<20; ++index) {
        float dist = map(eye);
        if (dist < 0.001) {
            shade = 1.;// - float(index)/20.;
            break;
        }
        eye += ray*dist;
    }

    fragColor = vec4(shade*(sin(iTime)*0.4 + 0.6), 
                     shade*(cos(iTime)*0.4 + 0.6), 
                     shade*(sin(0.5*iTime)*0.4 + 0.6), 
                     1.);
}
