void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.x)*2.0;
    uv.x-=(iResolution.y/iResolution.x);
   uv=abs(uv-.5) - iTime * .02; 
    if(iMouse.z>0.)uv.y += uv.x ;     
    float s = 0.05 + (sin(iTime*.05)+1.)*0.1;
    float k = mod(uv.x,s);
    fragColor = vec4(smoothstep( k*.98,k, abs(mod(uv.y,s)) ));
}
