vec4 vignette(inout vec4 col, vec2 uv, float intensity, float smoothness, vec3 tintCol, vec2 center, float roundness, float rounded)
{
    // Based on Unity 2020.1 implementation.
    vec2 dist = abs(uv - center) * intensity;
    dist.x *= mix(1.0, iResolution.x / iResolution.y, rounded);
    dist = pow(clamp(dist, 0.0, 1.0), vec2(roundness));
    float val = pow(clamp(1.0 - dot(dist, dist), 0.0, 1.0), max(smoothness, 0.001));

    col.rgb *= mix(tintCol, vec3(1.0), val);
    col.a = mix(1.0, col.a, val);

    return col;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Input
    float mouseEnable = float(iMouse.xy != vec2(0.0));
    vec2 mouseN = iMouse.xy / iResolution.xy;
    vec2 mouseCenter = abs(mouseN - 0.5);

    float intensity = 1.5 + (mouseEnable * mix(4.5, 0.0, mouseCenter.x * 2.0));
    float smoothness = 1.0;
    vec3 tintCol =  vec3(0.3, 0.2, 0.1);
    vec2 center = vec2(0.5, 0.5);
    float roundness = 1.0;
    float rounded = 0.0;

    if(texelFetch(iChannel1, ivec2(49,0), 0).x == 1.0)			// '1'
    {
        smoothness = 1.0 - mouseCenter.y * 2.0;
    }
    else if(texelFetch(iChannel1, ivec2(50,0), 0).x == 1.0)		// '2'
    {
        roundness = 1.0 + mouseCenter.y * 20.0;
    }
    else if(texelFetch(iChannel1, ivec2(51,0), 0).x == 1.0)		// '3'
    {
        rounded = mouseCenter.y * 2.0;
    }
    else if(texelFetch(iChannel1, ivec2(52,0), 0).x == 1.0)		// '4'
    {
        center = mouseN;
        intensity = 2.0;
    }


    // Image
    vec2 uv = fragCoord / iResolution.xy;
    vec4 col = texture(iChannel0, uv);
    col = vignette(col, uv, intensity, smoothness, tintCol, center, roundness, rounded);

    // Output to screen
    col = clamp(col,0.0,1.0);
    fragColor = vec4(col.xyz, 1.0);
}
