void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pos = (fragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;


    pos*=0.5;
    pos+=vec2(0.5);

	float vv = pos.y*pos.y;
	vv*=sin(pos.x*3.14);
	float v = sin(sin(pos.x*15.0)*4.0+(vv) *50.0 + iTime * 2.0);

	v+=0.65;
	float stime = 0.5+sin(iTime*4.0)*0.5;
	fragColor = vec4( v*1.3, 0.25+.3*v, 0.5, 1.0 ) * (1.0-stime*0.3);

    if (iMouse.z>0.5)
    	fragColor=vec4(v);

}
