void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    float oe = 1.0;
    float ue = -20.0;
    float sp = 0.5;
  	float b = abs(mod(iTime*sp, (oe-ue)*2.0)-(oe-ue))+ue;
    float a = pow(2.0,-b);

    float x = (uv.x - 0.5);
    float y = (uv.y - 0.5) * (iResolution.y/iResolution.x);
    x = x + -1.5; //* a;
    y += 0.0;// * a;


    vec2 po = vec2(x,y);

    float co = 0.0;
    vec2 n = vec2(0.0,0.0);
    while(n.y < iTime && co < iTime){
    	n = vec2(n.x*n.x - n.y*n.y, 2.*n.x*n.y) + po; //* 1.0/a;
        co = co + 1.0;
    }


    if (n.y < iTime ){
    	vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
        fragColor = vec4(col,1.0);
    }else{
    // Time varying pixel color


        fragColor = vec4(co/100.0,co/100.0,co/100.0,1.0);


        }


}
