#define third 0.3333333333

bool coloured(vec2 uv){

    return uv.x < third || uv.x > 2.*third || uv.y < third || uv.y > 2.*third;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy/iResolution.x;
    float zoom = mod(30.*iTime,109.);
    uv*= pow(0.99,zoom);
    fragColor = vec4(1);
    float its = log(iResolution.y)/log(3.);

    for(float i = 0.; i < its; i+=1.){
        if (coloured(uv)){
            uv = fract(3.*uv);
        }
        else{
        	fragColor = vec4(0,0,0,1);
            return;
        }
    } 


}
