#define w_frequency_x 0.25 //how fast water will move in x axis
#define w_frequency_y 0.35 //how fast water will move in x axis


#define w_distortion_factor 0.025 //distortion factor
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 base = texture(iChannel0, uv);   
    vec4 norm = texture(iChannel1, vec2(uv.x+(iTime*w_frequency_x),uv.y+(iTime*w_frequency_y)));
    vec4 base_distorted = texture(iChannel0, uv+ (norm.rg * w_distortion_factor));

    fragColor = mix(base, base_distorted+norm*0.25f, 1.f);

}
