void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = -1.0 + 2.0 * fragCoord.xy / iResolution.xy;
    p.x *= iResolution.x/iResolution.y;

    // animation	
	vec2 c = vec2(-0.05,.6805) + p;

    // iterate
    float di =  1.0;
    vec2 z  = vec2(0.0);
    float m2 = 0.0;
    vec2 dz = vec2(0.0);
    for( int i=0; i<8; i++ )
    {
        if( m2>1024.0 ) { di=0.0; break; }
		// Z' -> 2·Z·Z' + 1			
        // Z -> Z² + c			
        z = vec2( z.x - z.y, z.x ) + c;
        z /= tan(z.yx+iTime);
        m2 = dot(z,z);
    }

    fragColor = vec4(vec3( di ), 1.0 );
}
