#define A(COORD) texture(iChannel0,(COORD)/iResolution.xy)
float ln (vec3 p, vec3 a, vec3 b) {return length(p-a-(b-a)*min(dot(p-a,b-a),0.)/dot(b-a,b-a));}
void mainImage( out vec4 Q, in vec2 U )
{
    vec2 X = vec2(0.5);
    if (iMouse.z>0.) X = iMouse.xy/iResolution.xy;
    Q  =  A(U);
    vec4 pX  =  A(U + vec2(1,0));
    vec4 pY  =  A(U + vec2(0,1));
    vec4 nX  =  A(U - vec2(1,0));
    vec4 nY  =  A(U - vec2(0,1));
    vec3 n = normalize(vec3(pX.z-nX.z,pY.z-nY.z,1));
    vec3 r = reflect(n,vec3(0,0,-1));
    Q = 0.8+0.1*sin(19.*vec4(1,2,3,4)*(Q.z));
    float d = ln(vec3(X-.5,10)*iResolution.xyy,
                 vec3(U,0),vec3(U,0)+r)/iResolution.y;
    Q *= exp(-.5*d*d);
}

#define A(COORD) texture(iChannel0,(COORD)/iResolution.xy)

void mainImage( out vec4 Q, in vec2 U )
{
    vec2 X = 0.5*iResolution.xy;
    if (iMouse.z>0.) X = iMouse.xy;
    U-=X;
    float a = .001*sin(.1*iTime)/(1.+length(U-0.5*iResolution.xy)/iResolution.y);
    U *= mat2(cos(a),-sin(a),sin(a),cos(a));
    U *= .999;
    U+=X;
    Q  =  A(U);
    // Neighborhood :
    vec4 pX  =  A(U + vec2(1,0));
    vec4 pY  =  A(U + vec2(0,1));
    vec4 nX  =  A(U - vec2(1,0));
    vec4 nY  =  A(U - vec2(0,1));
    vec4 m = 0.25*(pX+nX+pY+nY);
    float b = .3;
    Q += (1.-b)*(0.25*vec4(

        (pX.z-nX.z) - 5.*Q.z*(pY.z-nY.z),
       	(pY.z-nY.z) + 5.*Q.z*(pX.z-nX.z),
        -pX.x+nX.x-pY.y+nY.y,1
    )- Q);


    Q = mix(Q,m,b);

    if (length(Q.xy)>0.) Q.xy = normalize(Q.xy);

    if(iFrame < 1) Q = sin(.1*U.xxxx);


}

