void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 palette[4];
    palette[0] = vec3(  8, 24, 32)/255.;
    palette[1] = vec3( 52,104, 86)/255.;
    palette[2] = vec3(136,192,112)/255.;
    palette[3] = vec3(224,248,208)/255.;

    vec4 res;
    res.xy = vec2(128,112); // "square" resolution
    res.zw = vec2(res.y*iResolution.x/iResolution.y,res.y); // widescreen resolution

    vec2 uv = fragCoord/iResolution.xy;
    uv.x=1.-uv.x; // optional mirror selfie mode
    uv = floor(uv*res.zw);

    int value = 0;
    if (abs(uv.x*2.-res.z) < res.x)
    {
	    float gray = dot(texture(iChannel0,uv/res.zw).rgb, vec3(0.2126,0.7152,0.0722));

        float dither = texture(iChannel1, uv/8.).r;
	    value = int(floor(gray*3.+dither));
    }

    fragColor = vec4(palette[value], 1);
}
