//draw a box use two corner point
float rect(vec2 uv, vec2 p1, vec2 p2)
{
    vec2 edge = abs((p1 + p2)*0.5 - uv);
    vec2 srect = smoothstep(edge- vec2(0.01), edge+ vec2( 0.01), abs(p1 - p2)*0.5);
    return srect.x*srect.y;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.y;

    	float rectbox = rect(uv, vec2(cos(5.0*iTime)*0.5+0.5),vec2(sin(iTime)*0.5+0.5));


    // Output to screen
    fragColor = vec4(rectbox);
}
