// variant of https://shadertoy.com/view/MllXWM


void mainImage( out vec4 O, vec2 U ) {
    O-=O;
    float k=0., v, l=k, t = mod(iTime,88.); t = min(t,88.-t);
    vec2  R = iResolution.xy,
          z = (U-.5*R)/R.y / pow(t,t/20.) - vec2(1.001105,0.300717),
          c = z;

    for (; k++ <40. && l<4.; )         // Mandelbrot iterations
         l = dot( z = mat2(z,-z.y,z.x) *z + c, z ), // z = z*z + c
         v = fwidth(l);

    if ( U.x < .5*R.x ) v = fwidth(l); // left: post derivative. right: inloop
    O +=  pow(v,.2); 
  //O -= .1*log(v);

  //if (O.x==0.) O++;   // test: difference is just fwidth undefined (thus 0)  at divergences
    if (int(U)==int(.5*R)) O.r ++;     // red separator
 }
