#define SHRP .0000001
#define THIC .25

#define PRa .35

#define COVER .3

float letterS(vec2);
float letterA(vec2);
float letterT(vec2);
float letterO(vec2);
float letterR(vec2);
float letterN(vec2);
float letterE(vec2);
float letterP(vec2);

void sator(inout vec2, inout float);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord-.5*iResolution.xy)*2./min(iResolution.x, iResolution.y);
    p *= 1.1;
    p+=1.; p*=.5;

    vec2 ps = p;
    float b = 1.;

    for (int i = 0; i<6; i++) sator(ps,b);

    vec3 col = b*vec3(ps,1);

    // Output to screen
    fragColor = vec4(col,1.0);
}

float letterO(vec2 p)
{
    float d = distance(p,vec2(.5));
    return smoothstep(.5,.5-SHRP,d)*smoothstep(.5-THIC,.5-THIC+SHRP,d);
}
float letterT(vec2 p)
{
    p = vec2(abs(p.x-.5),p.y);
    float sq = smoothstep(.5,.5-SHRP,p.x)*smoothstep(.5,.5-SHRP,abs(p.y-.5));
    float an = 1.-smoothstep(THIC*.5-SHRP,THIC*.5,p.x)*smoothstep(1.-THIC+SHRP,1.-THIC,p.y);
    return an*sq;
}

float letterA(vec2 p)
{
    p = vec2(.5-abs(p.x-.5),p.y);
    float sla = (p.x-(.5-THIC*.5)*p.y)/sqrt(1.+(.5-THIC*.5)*(.5-THIC*.5));
    float tr = smoothstep(.5,.5-SHRP,abs(p.y-.5))*smoothstep(0.0,SHRP, sla);
    float si = 1.-smoothstep(THIC-SHRP,THIC,sla)*smoothstep(THIC*.5-SHRP,THIC*.5,abs(p.y-.35));
    return si*tr;
}

float letterN(vec2 p)
{
    float li = 1.-smoothstep(THIC*.5-SHRP,THIC*.5,abs(((p.x-.5)+(p.y-.5)*(1.-THIC))/sqrt(1.+(1.-THIC)*(1.-THIC))))
    *smoothstep(THIC*.5-SHRP,THIC*.5,abs(abs(p.x-.5)-.5+THIC*.5));
    float sq = smoothstep(.5,.5-SHRP,abs(p.x-.5))*smoothstep(.5,.5-SHRP,abs(p.y-.5));
    return sq*li;
}

float letterE(vec2 p)
{
    float sq = smoothstep(.5,.5-SHRP,abs(p.x-.5))*smoothstep(.5,.5-SHRP,abs(p.y-.5));
    float dc = 1.-smoothstep(THIC-SHRP,THIC,p.x)*smoothstep(.25-.75*THIC+SHRP,.25-.75*THIC,abs(abs(p.y-.5)-.25+.25*THIC));
    return dc*sq;
}

float letterS(vec2 p)
{
    if (p.y>.5) {p.y=1.-p.y;p.x=1.-p.x;}
    float sq = smoothstep(.5,.5-SHRP,abs(p.x-.5))*smoothstep(.5,.5-SHRP,abs(p.y-.5));
    float ci = 1.-smoothstep(.25-.75*THIC+SHRP,.25-.75*THIC,abs(abs(p.y-.5)-.25+.25*THIC))*smoothstep(1.-THIC+SHRP,1.-THIC,p.x);
    vec2 q = p;
    if (q.y>.25+.25*THIC) q.y = .5+.5*THIC-q.y;
    float sla = dot(q,normalize(vec2(1,-1)));
    float del = sqrt(2.)*.5-sqrt(2.)*.5*THIC;
    float an = smoothstep(-THIC*.5,SHRP-THIC*.5,sla)
    *smoothstep(-THIC*.5,SHRP-THIC*.5,del-sla)
    *smoothstep(-THIC*.5,SHRP-THIC*.5,del-abs(dot(p-vec2(.5),normalize(vec2(1)))));
    //an = 1.;
    return an*sq*ci;
}

float letterR(vec2 p)
{
    float cir = p.x<1.-PRa?(abs(p.y-1.+PRa)):distance(p,vec2(1.-PRa));
    float ou = smoothstep(PRa-SHRP,PRa,cir);
    if (p.y<1.-2.*PRa+SHRP) ou *= smoothstep(-SHRP,0.,dot(normalize(vec2(1,1)),p-.5));
    ou = 1.-ou;
    float ir = 1.-smoothstep(THIC-SHRP,THIC,p.x)*smoothstep(PRa-THIC+SHRP,PRa-THIC,cir);
    float an = smoothstep(0.,SHRP,p.x)*smoothstep(0.,SHRP,p.y);
    float wg = 1.-smoothstep(THIC-SHRP,THIC,p.x)*smoothstep(THIC-SHRP,THIC,-dot(normalize(vec2(1,1)),p-.5))*smoothstep(PRa*2.-SHRP,PRa*2.,1.-p.y);
    return ir*ou*an*wg;
}

float letterP(vec2 p)
{
    float cir = p.x<1.-PRa?(abs(p.y-1.+PRa)):distance(p,vec2(1.-PRa));
    float ou = smoothstep(PRa-SHRP,PRa,cir);
    if (p.y<1.-2.*PRa+SHRP) ou *= smoothstep(THIC-SHRP,THIC,p.x);
    ou = 1.-ou;
    float ir = 1.-smoothstep(THIC-SHRP,THIC,p.x)*smoothstep(PRa-THIC+SHRP,PRa-THIC,cir);
    float an = smoothstep(0.,SHRP,p.x)*smoothstep(0.,SHRP,p.y);
    return ou*an*ir;
}

void sator(inout vec2 p, inout float b)
{
    if (!(abs(p.x-.5)>=.5||abs(p.y-.5)>=.5||b==0.))
    {
        p*=5.;
        int px = int(p.x);
        int py = int(p.y);
        p -= vec2(px,py);
        float lm;
        switch (py*10+px)
        {
            case 00:
            case 44:
                lm = letterS(p);
                break;
            case 01:
            case 10:
            case 43:
            case 34:
                lm = letterA(p);
                break;
            case 02:
            case 20:
            case 42:
            case 24:
                lm = letterT(p);
                break;
            case 03:
            case 30:
            case 41:
            case 14:
                lm = letterO(p);
                break;
            case 04:
            case 40:
            case 11:
            case 33:
                lm = letterR(p);
                break;
            case 13:
            case 31:
                lm = letterP(p);
                break;
            case 12:
            case 21:
            case 32:
            case 23:
                lm = letterE(p);
                break;
            case 22:
                lm = letterN(p);
                break;
            default:
                lm = 0.;
        }
        b *= 1.-COVER*(1.-lm);
    }
    p = cos(p*acos(0.)*2.);
    p *= p;
}
