void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord-0.5 * iResolution.xy) / iResolution.xy;

    uv.x *= iResolution.x / iResolution.y; // Fix Aspect Ratio

    float er = 1.5/iResolution.y; // Radius with scaleable AA
    float d = length(uv); // Distance calcuation

    // Time varying pixel color // Radius, AA
    vec3 c = fract(smoothstep(sin(iTime), 0.2-er, length(uv))-cos(iTime*.2+uv.xyx+vec3(2,2,6)));

    // Output to screen
    fragColor = vec4(c, 1.0);
}
