void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0. to 1.)
    vec2 nc = fragCoord/iResolution.xy;

    nc -= .5;
    nc.x *= iResolution.x/iResolution.y;

    float d = -length(nc)+0.5; //black hole effect 
    float c = d;

    if(d <.3) c += d; else c = 0.;

    fragColor = vec4(vec3(c),1.0);
}
