// original shader sources :
// https://www.shadertoy.com/view/WtB3zd
// https://www.shadertoy.com/view/WtB3zd

#define PI 3.1415923693
#define SINE(x) sin(.5 * PI * (x - .5)) + 1.
#define COSINE(x) 1. - cos(.5 * PI * x)

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = fragCoord/iResolution.xy;
    vec3 color;
    color.r = texture(iChannel0, uv - vec2(0.01, 0.)).r;
    color.g = texture(iChannel0, uv + vec2(0.01, 0.)).g;
    color.b = texture(iChannel0, uv).b;

    color.r = COSINE(color.r) * COSINE(color.r);

    color.b = COSINE(color.b + color.g) * COSINE(color.b);

    color.g = color.r * color.b;

    vec3 the_color = color;
    the_color.g = 0.0f;

    float maxrb = max( color.r, color.b );
    float k = clamp( (color.g-maxrb)*5.0, 0.0, 1.0 );

    float dg = color.g; 
    color.g = min( color.g, maxrb*0.8 ); 
    color += dg - color.g;

    fragColor = vec4(mix(color, the_color, k), 1.);
}
