void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float blur = pow (.0625 - cos(uv.x * 20.0 + iTime) * .0625, 2.);
    vec2 grad = vec2(blur, blur);
    if (iMouse.z >= .5) {
    	fragColor = textureGrad(iChannel0, uv, grad, grad);
    } else {
        fragColor =
            textureGrad(iChannel0, uv + .5 * vec2(blur, blur), grad, grad) * .25 + 
            textureGrad(iChannel0, uv + .5 * vec2(blur, -blur), grad, grad) * .25 + 
            textureGrad(iChannel0, uv + .5 * vec2(-blur, blur), grad, grad) * .25 + 
            textureGrad(iChannel0, uv + .5 * vec2(-blur, -blur), grad, grad) * .25;
    }
}
