// inspired from Txoka's https://www.shadertoy.com/view/tt3XDr
// https://en.wikipedia.org/wiki/Logistic_map

void mainImage( out vec4 O, vec2 u ){

    float z = STATE.x,              // zoom factor
          k = 5.,                   // tune blur ( < 2 ) vs noise ( > 2 )
          v, y, f = 0.;             // convergence tracker ( when > 0)

    vec2 R = iResolution.xy,
         U = u/R,
         M = length(iMouse.xy) < 10.// demo mode or mouse control 
                 ? vec2(.87)*.33 : iMouse.xy/R,
         V = max(U.x,U.y)<.33       // full view or inset
                ? U/.33 : M/.33+(U-.5)/z;


    for( float y0 = .1; y0 < 1.; y0+=.1 ) {
        y = y0 + .1*sin(iTime)/2.;  // sample initial state space
        for( int i=0 ; ++i<256; ) { // --- test convergence
            y *= 4.*V.x *(1.-y);
            v  = y - V.y;
            f += exp(-3e5*k*k*v*v); // diagram is where v converges towards 0, i.e. y converges to U.y
        }
    } f /= 10.;

    O = vec4(1.-.2*k*f);

    if ( max(U.x,U.y)<.33 ) {       // --- inset coloring
        U = abs(M-U)/.33;
        max(U.x,U.y) < .5/z ? O.gb -= .3 : O.rg -= .07; 
    }
}

// persistant states ( key tuning )

#define keyDown(a)  ( texelFetch(iChannel3,ivec2(a,1),0).x > 0.)

void mainImage( out vec4 O, vec2 U )
{
    if ( U != vec2(.5) ) discard;

    if ( iFrame < 1 )           // --- init
        O = vec4( 10, 0,0,0);   // O.x: zoom value 

    else {
        O = STATE;
        if (   keyDown(187)               // '+'  ( true ascii: 43 )
            || keyDown(107) ) O.x *= 1.2; // numpad
        if (   keyDown( 54)               // '-' azerty ( true ascii: 45 )
            || keyDown(189)               //     qwerty
            || keyDown(109) ) O.x /= 1.2; // numpad
    }
}
