vec4 nyan( in vec2 uv, in int index)
{
    ivec2 nyan = ivec2(40, 32);
    ivec2 ruv = ivec2(nyan.x * index, 0) + ivec2(uv * vec2(nyan));
	return texelFetch(iChannel0, ruv, 0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
	fragColor = nyan(uv, (iFrame / 5) % 6);
}
