float srcHue = 300. / 360.; // pink
float hueThreshold = .1;
float hueSpeed = .25;

vec2 texScale = vec2(1. / 6.4, 1.);
int animFrames = 15;


vec3 rgb2hsv(vec3 c) {
    vec4 K = vec4(0., -1. / 3., 2. / 3., -1.);
    vec4 p = mix(vec4(c.bg, K.wz), vec4(c.gb, K.xy), step(c.b, c.g));
    vec4 q = mix(vec4(p.xyw, c.r), vec4(c.r, p.yzx), step(p.x, c.r));

    float d = q.x - min(q.w, q.y);
    float e = 1.0e-10;
    return vec3(abs(q.z + (q.w - q.y) / (6. * d + e)), d / (q.x + e), q.x);
}


vec3 hsv2rgb(vec3 c) {
    vec4 K = vec4(1., 2. / 3., 1. / 3., 3.);
    vec3 p = abs(fract(c.xxx + K.xyz) * 6. - K.www);
    return c.z * mix(K.xxx, clamp(p - K.xxx, 0., 1.), c.y);
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 frameOffset = vec2(float((iFrame / animFrames) % 6), 0.);

    vec2 uv = (fragCoord / iResolution.xy + frameOffset) * texScale;
    vec3 rgb = vec3(texture(iChannel0, uv));
    vec3 hsv = rgb2hsv(rgb);

    float hueDiff = abs(hsv.x - srcHue);

    if (hueDiff < hueThreshold) {
        float destHue = fract(iTime * hueSpeed); // cycle dest hue over time
        vec3 destRGB = hsv2rgb(vec3(destHue, hsv.y, hsv.z));
        rgb = mix(destRGB, rgb, hueDiff / hueThreshold);
    }

    fragColor = vec4(rgb, 1);
}
