float grid(vec2 st, float res, float scale)
{
  vec2 grid = fract(st*res);
  return (step(scale,grid.x) * step(scale,grid.y));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy /iResolution.xy;

    float a = uv[0];
    float b = uv[1];

    vec2 manifold = vec2(
        pow(a, 2.0) + 
        pow(b, 3.0), 
        pow(a, 2.0) - 
        pow(b, 3.0));
    //ceil
    vec2 manifold2 = vec2(
        sin(pow(manifold[0], 2.0) + pow(manifold[1], 2.0)), 
        cos(pow(manifold[0], 2.0) + pow(manifold[1], 2.0)) );

    vec2 sur_def = vec2(
    clamp(dFdx(manifold[0]/ manifold2[0]), .001, .002),
    dFdy(manifold2) );

    float r = ( iTime );
    float x = grid(sur_def, 200.1 * tan(pow(r,.8)), .1);


    vec3 col = 0.6 + 0.3*cos(iTime+uv.xyx+vec3(0,8,16));  

    fragColor.rgb = vec3( col* x );
}
