float ll(vec2 uv, vec2 c0, vec2 c1, float t, float f, float a){
    return log(.001+(cos(t+length(uv-c0)*f)+1.)*(cos(t+length(uv-c1)*f)+1.))*0.01*a;
}

float bnc(float t){
    return abs(fract(t)-0.5)*2.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

//    vec2 b0 = vec2(0.2,0.6);
//    vec2 b1 = vec2(0.51,0.22);
    float t = iTime*.8;

    //float diff= 1.+8.*(iMouse.y/iResolution.y);
    float diff= .5+sin(t)*8.;

    vec2 b0 = vec2(bnc(t*0.04), bnc(t*.011));
    vec2 b1 = vec2(bnc(2.+t*.013), bnc(2.+t*.09));

    float a = ll(uv, b0, b1, t, 14., 1.2);
    float a2 = ll(uv+.4, b0, b1, t, 15., 1.);
    float a3 = ll(uv-.4, b0, b1, t, 16., 1.);

    float f = (a2- diff*a +a3)/.4;

    float c = (.1-(6.*f));

    // Output to screen
    fragColor = vec4(vec3(1.-c*c),1.0);
}
