void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));

    if (mod(iTime, 1.6) > 0.2 && mod(iTime, 1.6) < 1.0) col = 0.5 - col;

    float changevar = mod(uv.x + uv.y, 0.2);
    if (mod(iTime, 1.6) > 0.4) changevar = mod(uv.x - uv.y, 0.2);                      


    if (mod(iTime, 0.8) < 0.2 &&
        abs(changevar) < mod(iTime, 0.2) ||
        mod(iTime, 0.8) > 0.2 && mod(iTime, 0.8) < 0.4 &&
        abs(changevar) > mod(iTime, 0.2)) col = vec3(1);

    fragColor = vec4(col,1.0);
}
