#define PI 3.14159265359

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-0.5*iResolution.xy)/iResolution.y;

    uv *= 0.75;

    vec3 col = vec3(0);

    float a1 = smoothstep(-1., 0.75, sin(iTime * 0.5));
    float a2 = 0.1 + 0.3 * smoothstep(-0.75, -0.25, sin(iTime * 0.5));

    float t = iTime * 0.25;
    vec2 cp =  vec2(sin(t), cos(t));

    float scale = 4.;
    float size = mix(10., 10. * scale, a1);

    uv += 0.4 * cp * (1.5 - a1);

    vec2 pv = floor(uv * size) / size;
    vec2 fv = fract(uv * size);

    col = texture(iChannel0, pv * (a1 * scale + 1.) / scale).rgb;

    float sp = PI/1.5;
    float a = iTime;
    vec2 cp1 = 0.5 + 0.1 * vec2(sin(a), cos(a));
    vec2 cp2 = 0.5 + 0.1 * vec2(sin(a + sp), cos(a + sp));
    vec2 cp3 = 0.5 + 0.1 * vec2(sin(a + sp * 2.), cos(a + sp * 2.));

    float p1 = length(fv - cp1);
    float p2 = length(fv - cp2);
    float p3 = length(fv - cp3);
    float smth = 0.05;
    p1 = smoothstep(a2, a2 - smth, p1);
    p2 = smoothstep(a2, a2 - smth, p2);
    p3 = smoothstep(a2, a2 - smth, p3);

    col.r *= p1;
    col.g *= p2;
    col.b *= p3;

    fragColor = vec4(col,1.0);
}
