void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 mouse = iMouse.xy / iResolution.xy; //normalized

    vec3 colBG = vec3(1,0.5,0);
    vec3 colFG = vec3(1,0.1,1);

    vec3 col;
    float width = mouse.x; //15% of viewport
    float height = mouse.y;

    if(uv.s*uv.t < width*height) {
        col = colFG;
    } else { 
        col = colBG;
    }

    if ((uv.s < width) ^^ (uv.t < height)) {

          col = colFG;
    } else { 
        col = colBG;
    }
  //	if(uv.t < height) {
  //      col = colFG;
  //  } else { 
  //      col = colBG;
  //  }

    // Output to screen
    fragColor = vec4(col,1.0);
}
