float Hash21(vec2 p) {
    p = fract(p * vec2(459.4700892207039806094296464f, 314.1380652391393004493075896f));
    p += dot(p, p + 34.23);

    return fract(p.x * p.y);

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    vec3 col = vec3(0);

//    uv += iTime * 0.2;

//  - Parameters -----------
    float zoom = 10.0;
    float width = 0.1;
//  ------------------------

    uv *= zoom;
    vec2 gv = fract(uv) - 0.5;
    vec2 id = floor(uv);

    float n = Hash21(id); // random number 0 <> 1

    if (n < 0.5) { gv.x *= -1.0; }
    float d = abs(abs(gv.x + gv.y) - 0.5);
    float mask = smoothstep(0.01, -0.01, d - width);

    col += mask;
//    col += n;
//    if (gv.x > 0.48 || gv.y > 0.48) { col = vec3(1.0, 1.0, 1.0); }

    fragColor = vec4(col, 1.0); 
}
