void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy/1.4;

    float nTime=iTime*1.2;


    float col4=100.;

    float cnt=1.;

    for(float x1=0.;x1<3.;x1+=0.2)
    {
    for(float y1=0.;y1<3.;y1+=0.3)
    {
    float x2=uv.x+x1;
    float y2=uv.y+y1;

    float nFact=10.+50.*(cos(nTime*.13)-x2);
    float col1=sin((uv.x*y2)*nFact);
    float col2=cos((y2+sin(x2))*nFact);
    float col3=cos((x2*y2)*nFact);


    col4+=0.2126*(col1+col2+col3)+0.7152*col1+0.072*(col1+col2+col3);
    cnt+=1.;
    }
    }

    vec3 colmix=vec3(sin(nTime/10.),1.,1.);
	fragColor = vec4(vec3(col4/cnt)*colmix,1.);

}
