void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;

    float vectorLen = length(uv);
    float r=0.1;
    float r2=0.11;

    float m = smoothstep(r, r-0.01, vectorLen);
    float m2 = smoothstep(r2, r2-0.01, vectorLen);

    float ms = m2-m;
    vec3 col = vec3(1,0,0) * ms;


    // Output to screen
    fragColor = vec4(col, 1.0);
}
