// golfing 952 chars https://shadertoy.com/view/Ws2BW1
// reproducing Erlend Robaye's https://www.facebook.com/groups/procgenart/permalink/1991283661002453/
// Complex op from https://shadertoyunofficial.wordpress.com/2019/01/02/programming-tricks-in-shadertoy-glsl/

#define V vec2//
#define D(Z)     mat2( Z, Z.y, -Z ) * a / dot(a,a)        //
#define E(Z)     exp(Z).x * cos( (Z).y + V(0,11) )        //
#define I        V( -z.y, z )                             //
void mainImage(out vec4 O, vec2 u) {
    V R  = iResolution.xy, z,a,
    Z = 1.8 * ( u - (R-R.y)/2. ) / R.y + V(-.2,2.2);
    if ( abs(Z.x-.7) < 1.  )
        for( int i, k=0 ; k++ < 81;
             abs(z.x) < .1 ? O += .012 : O
           )
          { z = Z +  V(k%9-2,k/9-2) *.2 / R.y;
            for ( i=0; i++ < 4;) z = D( E(a=z) );
            a = z; a.x--;
            z = E(I) - E(-I);
            for ( z = D( -.5* I ) ; i++ < 9;
                  z = E( z + 6.*I ) )
                z = V( log(length(z)), atan(z.y,z.x) );
          }
}
