void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  	vec2 uv = fragCoord.xy / iResolution.xy;

    fragColor = texture(iChannel0, uv);
}

vec4 ssamp( vec2 uv, float oct )
{
    uv /= oct;

    vec4 noise = 0.254*(1.-texture(iChannel2, 3.4*uv - 0.025*iTime))
       + .1*(texture(iChannel2, 3.3*uv + 30.*iTime));

    return noise;
}

vec2 hash2( float n ) { return fract(sin(vec2(n,n+1.0))*vec2(43758.5453123,22578.1459123)); }
vec2 e = vec2(1./256., 0.);
vec4 dx( vec2 uv, float oct ) { return (ssamp(uv+e.xy,oct) - ssamp(uv-e.xy,oct)) / (2.*e.x); }
vec4 dy( vec2 uv, float oct ) { return (ssamp(uv+e.yx,oct) - ssamp(uv-e.yx,oct)) / (2.*e.x); }


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float oct = 1.8;
    vec2 curl1 = 0.7 * vec2(dy(uv,oct).x, -dx(uv,oct).x);

    vec2 curl = .0005 * curl1;



    vec2 wind = 0.0025*vec2(.45 + .1*sin(log(max(iTime,1.)))*3.,sin(iTime*2.0)*.75+0.75); // grav and wind
    vec2 rand = 0.001*(hash2(iTime)-0.5);

    fragColor = 0.999 * texture(iChannel0, uv + curl - rand - wind );

    float d = length(fragCoord-iResolution.xy/(3.+iTime/5.))*(sin(iTime*2.)*2.5+3.5);
    fragColor += .015*smoothstep(60.0,40.0,d);
}
