  //=============//
 // Final Blend //
//=============//

void mainImage(out vec4 color, vec2 coord) {
	vec2 uv = coord / iResolution.xy;

    vec4 original = texture(iChannel0, uv);

    uv = scale_uv(uv, vec2(RENDER_SCALE), vec2(0.5));
    vec4 blurred = texture(iChannel1, uv);
    blurred = pow(blurred, vec4(1.0 / GAMMA));

    color = mix(original, blurred, 1.0);
}

/*
    Doom 2016 does a fast real-time Bokeh
    where the half-resolution framebuffer
    is blurred with a circular average
    (tapped fragments in a circular pattern,
    no Gaussian used) and then a second blur
    is performed where the colours are
    blended with a colour "max" operation
    (brightest RGB components kept).
*/

  //========//
 // Macros //
//========//

#define BLUR_AMOUNT (iMouse.z > 0.0 ? (iMouse.x / iResolution.x) * 2.0 : 1.0)

  //===========//
 // Constants //
//===========//

const float PI = 3.14159;
const float GAMMA = 2.2;

const float RENDER_SCALE = 0.5;
//const float BLUR_AMOUNT = 1.0;

const int BOX_BLUR_SAMPLES = 13;
const vec2 BOX_BLUR_SCALE = vec2(2);
const vec2 BOX_BLUR_OFFSET = float(BOX_BLUR_SAMPLES / 2) * BOX_BLUR_SCALE;
const float BOX_BLUR_ACCUM = 1.0 / float(BOX_BLUR_SAMPLES);

  //===========//
 // Functions //
//===========//

vec2 scale_uv(vec2 uv, vec2 scale, vec2 center) {
    return (uv - center) * scale + center;
}

vec4 box_blur(sampler2D sp, vec2 uv, vec2 dir) {
	vec4 result = vec4(0.0);
    uv -= dir * BOX_BLUR_OFFSET;

    for (int i = 0; i < BOX_BLUR_SAMPLES; ++i) {
        result += texture(sp, uv);
        uv += dir * BOX_BLUR_SCALE;
    }
    result *= vec4(BOX_BLUR_ACCUM);

    return result;
}

  //===============//
 // Preprocessing //
//===============//

void mainImage(out vec4 color, vec2 coord) {
	vec2 ps = vec2(1) / iResolution.xy;
    vec2 uv = coord * ps;

    uv = scale_uv(uv, vec2(1.0 / RENDER_SCALE), vec2(0.5));

	color = texture(iChannel0, uv);
    color = pow(color, vec4(GAMMA));

	color.a = 1.0;
}

  //=================//
 // First Blur Pass //
//=================//

const vec2 OFFSETS[] = vec2[](
	vec2(0, 0),
    vec2(3, 0),
    vec2(-3, 0),
    vec2(0, 3),
    vec2(0, -3),
    vec2(2, 2),
    vec2(-2, 2),
    vec2(-2, -2),
    vec2(2, -2)
);

const float SCALE = 1.0;

void mainImage(out vec4 color, vec2 coord) {
    vec2 ps = vec2(1) / iResolution.xy;

    //color = box_blur(iChannel0, uv, vec2(ps.x, 0));

    color = vec4(0);

    for (int i = 0; i < OFFSETS.length(); ++i)
        color += texture(iChannel0, (coord + OFFSETS[i] * SCALE * BLUR_AMOUNT) * ps);
	color /= float(OFFSETS.length());

    color.a = 1.0;
}

  //==================//
 // Second Blur Pass //
//==================//

const vec2 OFFSETS[] = vec2[](
	vec2(1, 1),
    vec2(-1, 1),
    vec2(1, -1),
    vec2(-1, -1)
);

const float SCALE = 1.0;

void mainImage(out vec4 color, vec2 coord) {
    vec2 ps = vec2(1) / iResolution.xy;
    vec2 uv = coord * ps;

    //coord = scale_uv(coord, vec2(0.5), iResolution.xy * 0.5);

    color = vec4(0);

    for (int i = 0; i < OFFSETS.length(); ++i) {
        vec2 c = coord;
        c += OFFSETS[i] * SCALE * BLUR_AMOUNT;
        c *= ps;
        color = max(color, texture(iChannel0, c));
    }

    color.a = 1.0;
}
