void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 ouv = fragCoord/iResolution.xy;

	vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;               
    vec2 st = vec2(atan(uv.x, uv.y), length(uv));    
    uv = vec2(st.x / 6.2831 + iTime * 5.0 - st.y * 40.0, st.y);    
    float smf = 1.5*fwidth(uv.x);                 
    float m = fract(uv.x);    

    vec3 bufCol = texture(iChannel0, ouv).xyz;    
    float colIntence = (bufCol.x + bufCol.y + bufCol.z)*0.6;

    float mask = smoothstep(0., smf, abs(m-.5) - colIntence*0.4);    
    vec3 col = mix(vec3(1.), vec3(0.88, 0, 0.52), mask);   

    fragColor = vec4(col, 1.0);        
}
