void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy - vec2(0.25, 0.25);
    float x=sin(uv.x)*sin(uv.y);
   	vec2 center = vec2(-1,-1.0);

    float d=distance(center,uv.xy);


    float angle=atan((uv.y)/(uv.x));
    angle=degrees(angle); //convert angle
    angle+=270.; //Move angle to (0,360)

    angle=mod(angle,90.*d*0.01*(abs(sin(iTime)+4.0)/5.0));//abs(sin(iTime))/10.0);
    d=(d)*(angle);
    //d/=2.;//scale the density

    // Output to screen
    // removing the texture() matrix just outputs the wavy pattern
    // removing the vec4() just outputs the extension effect
    fragColor = vec4(vec3(d),0.1)*texture(iChannel0, uv*2.0);

    // extension effect
    // fragColor = texture(iChannel0, uv*2.0);

}
