void mainImage( out vec4 color, in vec2 coord )
{
    vec2 uv = coord/iResolution.xy;
    color = vec4(1.0);

    float freq = uv.x;
    float n = iChannelResolution[0].x;
    freq = floor(freq*n)/n;

    vec4 fft = texture(iChannel0,vec2(freq,.25));
    color = smoothstep(
          0.0
        , 0.0
        , fft-vec4(uv.y)
    );

    float waveform = texture(iChannel0,vec2(freq,.75)).x;
    color.g = smoothstep(
          0.0
        , 1.0
        , 1. - abs( waveform-uv.y ) * iResolution.y / 4.
    );
}
