float hash(float seed)
{
    return fract(sin(seed)*43758.5453 );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float sa = hash( dot( fragCoord, vec2(12.9898, 78.233) ) + 1113.1*float(iFrame) );
    vec3 col = vec3(sa);
    // Output to screen
    fragColor = vec4(col,1.0);
}
