//from: https://blog.demofox.org/2012/09/24/bias-and-gain-are-your-friend/

const float BIAS = 0.99;
const float GAIN = 0.01;


float getBias(float bias, float t)
{
	return (t / ((( (1.0/bias) - 2.0 ) * (1.0 - t)) + 1.0));
}

float getGain(float gain, float t)
{
    if(t < 0.5){
    	return getBias(t * 2.0, gain)/2.0;
    } else {
    	return getBias(t * 2.0 - 1.0,1.0 - gain)/2.0 + 0.5;
    }
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;


    vec3 color1 = vec3(0.4, 0.06, 0.01);
    vec3 color2 = vec3(0.1, 0.3, 0.6);


    float i = getBias(BIAS, uv.y) * getGain(GAIN, uv.y);

    vec3 col = mix(color1, color2, i );


    fragColor = vec4(col,1.0);
}
