#define RADIUS		100.0

void mainImage(out vec4 fragColor, in vec2 fragCoord){
	float dist = distance(fragCoord, iResolution.xy*0.5);
    if (dist < RADIUS){
    	fragColor = vec4(1.0, 0.0, 0.0, 1.0);
    } else {
    	fragColor = vec4(1.0, 1.0, 1.0, 1.0);
    }
}
