#define Q(a)a=length(a)*sin(vec2(-.2,1.4)+mod(atan(a.y,a.x),.4))
void mainImage(out vec4 O, in vec2 C)
{
    O-=O;
	O.z+=sin(iTime);
    for(float e,g,j,i;i<30.;i++)
    {
        vec3 p=g*vec3(C.xy/iResolution.y-.3,1);
        for(j=0.;j<5.;j++)
        {
            Q(p.xy);Q(p.yz);p.z-=8.;
        }
        g+=e=dot(abs(p),vec3(7,-5,sin(iTime*.5)+3.))*.1;
        O+=(e<.01)?.7/i:0.;
    }
}
