const float gridSize = 8.0;

const float sqrt3 = 1.732050807568877;
const vec2 halfSqrt3 = vec2(0.5, 0.866025403784439);

float sdHexagon(in vec2 p) {
    p = abs(p);
    return max(dot(p, halfSqrt3), p.x);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 r = vec2(1.0, sqrt3)*vec2(gridSize);
    vec2 gridACoord = mod(fragCoord, r)/gridSize - halfSqrt3;
    vec2 gridBCoord = mod(fragCoord+0.5*r, r)/gridSize - halfSqrt3;
    vec2 nearestGridCoord = (dot(gridACoord,gridACoord) < dot(gridBCoord,gridBCoord)) ? gridACoord : gridBCoord;
    float dither = 1.0-sdHexagon(nearestGridCoord)*2.0;

    vec4 color = texture(iChannel0, fragCoord/iResolution.xy);
    float gray = dot(color, vec4(0.3,0.59,0.11,0));
    bool bw = gray > dither;

    fragColor = vec4(vec3(bw ? 1.0 : 0.0), 1.0);
}
