void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord/iResolution.xy - 0.5) * vec2(1.0, iResolution.y/iResolution.x);

    float myTexture = 1.0 - length(uv)*4.0;
    float emissiveDuration = 2.0;
    vec3 emissiveColor = vec3(1.0,0.7,0.0);
    float emissiveStrength = smoothstep(1.0,0.0,fract(iTime*(1.0/emissiveDuration)));
    vec3 emissiveTotal = emissiveColor * emissiveStrength * myTexture;

    // Output to screen
    fragColor = vec4(emissiveTotal,1.0);
}
