float Random(vec2 p) {
	p = fract(p*vec2(123.34, 456.21));
    p += dot(p, p+45.32);
    return fract(p.x*p.y);
}

float T(float a) {
    float random = Random(vec2(a*532.43, a*784.34));
    float interval = clamp(random, 0.3, 0.6);
	return (sin(iTime*a*1.4*random)+1.)/2.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0.);

    // scaling
    uv.x *= 2.;

    // thickness
    float width = 0.06;
    float edge = 0.001;

    // bars
    for(float i=.0; i<16.; i=i+2.){
        float m1 = smoothstep(width+edge, width, abs(uv.x-.8+(i/10.)));
        col += m1 * T(2.+(i*.05));
    }

    // colors
    col.r -= T(4.);
    col.g -= T(5.);
    col.b -= T(6.);

    fragColor = vec4(col,1.0);
}
