#define PI 3.141592
////////////////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////////////////
mat2 r(float a){return mat2(cos(a),sin(a),-sin(a),cos(a));}


float ob(vec2 uv,float offset,float dv) {
    return length(floor(abs(uv*r(iTime*dv+offset+length(uv)))*r(
                                        sqrt(abs(cos(iTime*uv.x)*sin(iTime*uv.y))))

       ));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{

        vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;

       float r = ob(uv,0.,1.);
       float g = ob(uv,2.*PI/3.,1.1);
       float b =ob(uv,4.*PI/3.,.9);

    vec3 col =  mix(vec3(0.1),vec3(r,g,b),vec3(smoothstep(0.91,0.2,length(uv))));
    fragColor = vec4(col,1.0);
}
