void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = texelFetch(iChannel0, ivec2(fragCoord / scale), 0);
}

int get(ivec2 pos) {
    return int(texelFetch(iChannel0, pos, 0).x);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    fragColor = vec4(get(ivec2(fragCoord)));
    if(iMouse.z > 0.01) {
        vec2 distance = abs(ceil(fragCoord.xy - ceil(iMouse.xy / scale)));
        if(distance.x == 0. && distance.y == 0.) {
            fragColor = vec4(1.);
        }
    } else if(floor(iTime * stepsPerSecond) > floor((iTime - iTimeDelta) * stepsPerSecond)) {
        int sum =
            get(ivec2(fragCoord) + ivec2(-1.,1.)) +
            get(ivec2(fragCoord) + ivec2(0.,1.)) +
            get(ivec2(fragCoord) + ivec2(1.,1.)) +
            get(ivec2(fragCoord) + ivec2(-1.,0.)) +
            get(ivec2(fragCoord) + ivec2(1.,0.)) +
            get(ivec2(fragCoord) + ivec2(-1.,-1.)) +
            get(ivec2(fragCoord) + ivec2(0.,-1.)) +
            get(ivec2(fragCoord) + ivec2(1.,-1.));
        if(sum < 2 || sum > 3)
            fragColor = vec4(0.);
        if(sum == 3)
            fragColor = vec4(1.);
    }
}
