// variant of forks:          https://shadertoy.com/view/wsKGWD
// variant of modifiers zoo:  https://shadertoy.com/view/3dK3WW
// see also interactive mixer: https://www.shadertoy.com/view/tsG3zG

void mainImage( out vec4 O, vec2 u )
{
    vec2 R = iResolution.xy, 
         U = u / R.y, 
         Z = CS( 1e2*U.x ), 
       //Z = CS( 1e2*length(U) ), 
        Z0,V; 

    float a = 0.;
    for(int i=0; i<100; i++) {
        V  = U - T(i).xy;   
        //a += logZ(V).y * T(i).z;   // adding fork
        a += smoothstep(0., TAU, 2.*mod( logZ(V).y+3.14-T(i).w, TAU) )*TAU  * T(i).z; // adding oriented fork 
    }
  //O = vec4( fract(a/TAU) ); 
    Z  = mulZ( Z, CS(a));
    if keyToggle(65) {               // without strip modulation
       Z = CS(a);  
       O = vec4( .5+.5 * Z, 0, 1);           // complex to colors
     //O = sqrt(O.xxxx);  
    } 
    else {                           // with strip modulation
       O = vec4( .5+.5 * Z, 0, 1);           // complex to colors
       O = sqrt(O.xxxx);                     // BW images in sRGB
    }
}

// manage nodes location: init, anim, mouse interaction

#define hash4(x)        fract( 1e4* sin( 1e4* (x) * vec4(1,.111,.234,.731) ))

void mainImage( out vec4 O, vec2 U )
{
    U -= .5;
    if (U.y > 0.) return;

    vec2 R = iResolution.xy,
         M = iMouse.xy/R.y;

    if (iFrame==0) {
        O     = hash4(U.x);                                     // random node pos + rot sign + angle
        O.xy *= R/R.y; O.z = sign(O.z-.5); O.w *= TAU; 
        return;
    }

    O = T(U.x);

    // for better mouse control, cf https://www.shadertoy.com/view/tsG3zG
    if (iMouse.z > 0. && length(M-O.xy) < .03 ) O.xy = M;       // mouse moves node


    if  keyToggle(32)                                           // autodemo animation
            O.w += iTimeDelta /3.;
        else if (length(M)<20./R.y)  
            O.xy += CS( iTime + hash4(U.x+.1).z*6.28 ) / R.y; 
}
