float colDivisions = 10.0;
float rowDivisions = 10.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float aspectRatio = iResolution.y / iResolution.x;


    // checkerboard "colors"
    vec3 whiteCol = texture(iChannel0, uv).rgb; //vec3 whiteCol = vec3(1.0, 1.0, 1.0);
    vec3 blackCol = texture(iChannel1, uv).rgb; //vec3 blackCol = vec3(0.0, 0.0, 0.0);

    //fix aspect ratio for checkers
    uv.y *= aspectRatio;

    float fixedColsDiv = colDivisions / aspectRatio;
    float fixedRowsDiv = rowDivisions / aspectRatio;

    float yeet = colDivisions/rowDivisions;

	// create checkers in each direction
    uv.x *= fixedColsDiv;
    uv.y *= fixedRowsDiv;

    float t = mod(floor(uv.x) + floor(uv.y), 2.0);

    vec4 Layer0 = vec4(whiteCol, 1.0); // white background
    vec4 Layer1 = vec4(whiteCol, t); // white boxes
    vec4 Layer2 = vec4(blackCol, 1.0 - t); // black boxes 


    //if you get to a place where they would be cut off, don't draw them
    //if uv.x is greater than colDivisions then uv.x will be 0 (to make alpha 0)
    Layer1.a *= 1.0 - step(colDivisions, uv.x);
    Layer1.a *= 1.0 - step(rowDivisions, uv.y);
    Layer2.a *= 1.0 - step(colDivisions, uv.x);
    Layer2.a *= 1.0 - step(rowDivisions, uv.y);

    vec4 Composite = mix(Layer0, Layer1, Layer1.a);
    Composite = mix(Composite, Layer2, Layer2.a);

    // Output to screen
    fragColor = vec4(Composite);
}
