float xpos = -0.0452407411;
float ypos = 0.9868162204352258;
const float ratio = 288.0 / 512.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float r = 4.0 * pow(0.755,iTime);
    int m = 250 + 24 * int(iTime);

    float scale = 512.0 / r;

    float c_re = fragCoord.x / scale + (xpos - r / 2.0);
    float c_im = fragCoord.y / scale + (ypos - r / 2.0 * ratio);

    float x = 0.0;
    float y = 0.0;
    float x2 = 0.0;
    float y2 = 0.0;
    int iteration = 0;

    while(x2 + y2 <= 4.0 && iteration < m) {
        y = (x + x) * y + c_im;
        x = x2 - y2 + c_re;
        x2 = x * x;
        y2 = y * y;
        iteration++;
    }

    float h = 1.0 - mod(float(iteration) / 200.0, 1.0) ;

    vec3 rgb = clamp( abs(mod(h*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 ) * float(iteration < m);

    // Output to screen
    fragColor = vec4(rgb,1.0);
}
