void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy*2.-1.;

    float timeb = iTime * (1.0 +min(max(log(iTime/100.),0.0),4.0));
    float a = trunc(timeb);
    float b = fract(timeb);

    float time = a *1.0 + smoothstep(0.0, 1.0-min(max(log(iTime/100.),0.0),0.95), b*1.)+sin(log(iTime))*0.0001;


    float t = time*(cos(time*2.11+2000.)*0.0123623)+0.3;
    float t2 = time*(sin(time*2.13)*0.0347332)+0.6;
    float l = length(uv)*1.;
    float dist1 = smoothstep(sin(t+uv.x*sin(uv.x*t))*0.523625+0.666223,   cos(t+uv.y*cos(uv.y*t2))*0.52355+0.75364   + sin(t)*0.55, l);
    float dist2 = smoothstep(sin(t+uv.x*sin(uv.x*t*t2))*0.52355+0.565325, cos(t+uv.y*cos(uv.y*t2))*0.452355+0.75346 + sin(t)*0.14, l);
    float dist3 = smoothstep(sin(t+uv.x*sin(uv.x*t))*0.51666+0.566236, cos(t+uv.y*cos(uv.y*t*t2))*0.122355+0.834635 + sin(t)*0.21, l);

    vec3 col1 = vec3(dist1, dist2, dist3);
    vec3 col2 = vec3(fract(cos(uv.x*0.2+t)+fragCoord.x), fract(atan(uv.y*0.5-t+fragCoord.y)), fract(sin(uv.y*uv.x*0.1+t2*0.2+fragCoord.x+fragCoord.y)));

    vec3 col = mix(col1, col2, sin(t+l*10.)/6.+0.2);

    fragColor = vec4(col,1.0);
}
