#define LAYERS 2.0
#define FREQUENCY 4.0
#define DECAY 0.6
#define SOFTNESS 0.1
#define WASHOUT 0.3
#define POWER 2.5

float smin(in float a, in float b, in float s) {
    float i = clamp(0.5 + 0.5 * (b - a) / s, 0.0, 1.0);
    return mix(b, a, i) - s * i * (1.0 - i);
}

vec2 getCellPoint(in vec2 p) {
    return 0.5 + 0.5 * vec2(sin(p.x + p.y * 2.0 + iTime), cos(p.y - p.x + 2.0 * iTime));
}

float voronoi(in vec2 uv, in float scale) {
    uv *= scale;
    vec2 cellLocation = floor(uv);

    float minDist = 1000000000.0;
    for (int i=-1; i < 2; i++) {
        for (int j=-1; j < 2; j++) {
            vec2 curCellLocation = cellLocation + vec2(i, j);
            vec2 curCellPoint = curCellLocation + getCellPoint(curCellLocation);
            minDist = smin(minDist, length(uv - curCellPoint), SOFTNESS);
        }
    }

    return minDist;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y;
    vec3 color = vec3(0.0);
    float totalScale = 0.0;
    float scale = 1.0;

    for (float layer=0.0; layer < LAYERS; layer++) {
        float voronoiSample = pow(voronoi(uv, FREQUENCY / scale), POWER);
        color += mix(vec3(0.0, 0.7, 0.9), vec3(1.0 + WASHOUT), voronoiSample) * scale;
        totalScale += scale;
        scale *= DECAY;
    }

    color /= totalScale;
    fragColor = vec4(color, 1.0);
}
