void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col = texture(iChannel0, uv).rgb;
    float di = 10000.0 / (50.0+distance(gl_FragCoord.xy, vec2(300.0, 250.0)));
    float d = mod(di + col.r * 50.0 + iTime * 20.0, 100.0);
    float cut = 30.0;
    float shift = (smoothstep(cut - 3.0, cut, d) - pow(smoothstep(cut+1.0, cut+30.0, d), 0.3)) * 0.45
        * fract(d * 0.1);
    col.r += shift;
    col.g += shift * 0.8;        
    fragColor = vec4(col, 1.0);
}
